const TechCarousel: React.FC<TechCarouselProps> = ({ filter }) => {
  const { ref, isIntersecting } = useIntersectionObserver({ threshold: 0.1 });
  const [activeIndex, setActiveIndex] = useState(-1);
  const [autoScrollEnabled, setAutoScrollEnabled] = useState(true);
  const intervalRef = useRef<NodeJS.Timeout | null>(null);
  
  // Filter tech icons by category if filter is specified
  const filteredTechIcons = filter ? techIcons.filter(tech => tech.category === filter) : techIcons;
  
  // Auto-scroll carousel
  useEffect(() => {
    if (autoScrollEnabled && isIntersecting) {
      intervalRef.current = setInterval(() => {
        setActiveIndex(prev => (prev + 1) % filteredTechIcons.length);
      }, 3000);
    }
    
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    }
  }, [activeIndex, isIntersecting, autoScrollEnabled, filteredTechIcons.length]);
  
  const sectionRef = useRef<HTMLElement>(null);
  
  // Use effect to sync refs
  useEffect(() => {
    if (sectionRef.current) {
      // @ts-ignore - we know this works even if TypeScript complains
      ref.current = sectionRef.current;
    }
  }, [ref]);
  
  return (
    <section ref={sectionRef} className="py-16 bg-gray-50 dark:bg-gray-900">
      <div className="container mx-auto px-4 md:px-6">
        <h2 
          className={cn(
            "text-3xl md:text-4xl font-bold text-center mb-4 transition ease-out duration-500",
            isIntersecting ? "opacity-100 translate-y-0" : "opacity-0 -translate-y-4"
          )}
        >
          My Tech <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600 dark:from-blue-400 dark:to-indigo-400">Stack</span>
        </h2>
        
        <p 
          className={cn(
            "text-gray-600 dark:text-gray-300 text-center mb-12 max-w-2xl mx-auto transition ease-out duration-500 delay-200",
            isIntersecting ? "opacity-100" : "opacity-0"
          )}
        >
          These are the technologies I work with to create powerful web applications.
          Hover over each icon to learn more.
        </p>
        
        <div 
          className={cn(
            "grid grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-8 gap-6 max-w-5xl mx-auto transition ease-out duration-700 delay-300",
            isIntersecting ? "opacity-100 translate-y-0" : "opacity-0 translate-y-10"
          )}
          onMouseEnter={() => setAutoScrollEnabled(false)}
          onMouseLeave={() => setAutoScrollEnabled(true)}
        >
          {filteredTechIcons.map((tech, index) => (
            <div 
              key={tech.name}
              className="relative group"
              onMouseEnter={() => setActiveIndex(index)}
              onMouseLeave={() => setActiveIndex(-1)}
            >
              <div className={cn(
                "flex flex-col items-center justify-center p-4 bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-100 dark:border-gray-700 transition-all duration-300 hover:shadow-md",
                activeIndex === index ? "ring-2 ring-blue-500 dark:ring-blue-400 scale-110" : ""
              )}>
                <div className="mb-2">
                  {tech.icon}
                </div>
                <span className="text-xs font-medium text-gray-700 dark:text-gray-300">{tech.name}</span>
                
                {/* Tooltip */}
                {activeIndex === index && (
                  <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 w-48 p-2 bg-gray-800 dark:bg-gray-700 text-white text-xs rounded shadow-lg z-10 animate-fadeIn">
                    <div className="relative">
                      <p className="font-medium mb-1">{tech.name}</p>
                      <p className="text-gray-300 text-[10px]">{tech.description}</p>
                      <div className="absolute w-3 h-3 bg-gray-800 dark:bg-gray-700 transform rotate-45 left-1/2 -bottom-[6px] -translate-x-1/2"></div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default TechCarousel;